INSERT INTO `info` (`id`, `title_kz`, `title_ru`, `title_url_kz`, `title_url_ru`, `content_kz`, `content_ru`, `date`, `created_at`, `parent`) VALUES
(15, 'Мәслихат жұмысы туралы ақпарат', 'Информация о работе маслихата', 'mslihat-jmysy-turaly-aparat', 'informaciya-o-rabote-maslihata', '<p style=\"text-align:center\"><img src=\"http://enbekshikazak-maslikhat.kz/admin/uploads/images/91748206_R13.png\" style=\"height:103px; width:150px\" /></p>\r\n\r\n<p style=\"text-align:center\"><strong>Еңбекшіқазақ аудандық мәслихаттың 2018 жылы атқарған жұмысы туралы мәслихат хатшысы Б.Ахметовтың есебінің тезистері</strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp; 2019 жыл, ақпан</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Ардақты ағайын!</strong></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>&nbsp;&laquo;Еңбегің өнімді болса, көңілің сенімді болар&raquo;</strong> демекші,&nbsp; аудандық мәслихаттың регламенті негізінде назарларыңызға мәслихат хатшысының халық алдындағы есебін, яғни аудандық мәслихаттың 2018 жылы атқарған жұмысы туралы&nbsp; есебін ұсынамын.</p>\r\n\r\n<p style=\"text-align:center\"><strong>1. 6-ШАҚЫРЫЛЫМНЫҢ САПАЛЫҚ ҚҰРАМЫ</strong></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2016 жылы 20 наурызда өткен саяси науқанда өздеріңіз артқан сенімді арқалап, жергілікті өкілді билік органы болып табылатын аудандық мәслихаттың 6-шақырылымының депутаты атанған <strong>23 азамат</strong> өз өкілеттіктерін жүзеге асыру үшін сыбана еңбек етуде.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Депутаттық корпустың <strong>47,8 %-ын</strong> (11 адам) ауданымыздың экономикасына үлес қосып жүрген кәсіпкерлер, <strong>30,4%-ын</strong> (6 адам), мемлекеттік мекеме басшылары, <strong>8,7 %-ын</strong> (2 адам) мекеме басшыларының орынбасарлары, <strong>13% -ын</strong> (3 адам) өзге сала өкілдері құрайды.</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>2. &nbsp;МӘСЛИХАТ СЕССИЯСЫ</strong></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Заңға сәйкес мәслихаттардың қызметінің негізгі нысаны <strong>сессия</strong> болып табылады және дәл сессия арқылы біз атқарушы органдармен бірлесе отырып, Елбасымен белгіленген міндеттердің, бағдарламалардың іске асырырылуы мен орындалуын қарастырып, көзделген межелерге қол жеткізе аламыз.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Сессияларға депутаттардың орташа қатысуы қазір <strong>86,9%</strong> құрап отыр.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2018 жылы аудандық мәслихат өз өкілеттілігінің шегінде <strong>17 сессия</strong> өткізіп, <strong>93 шешім</strong>&nbsp; қабылдады.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;Бүгінгі таңда &laquo;Нормативтік құқықтық актілер туралы&raquo; Қазақстан Республикасының Заңына сәйкес құқықтық нормаларын&nbsp; белгілейтін аудандық мәслихаттың <strong>24 нормативтік құқықтық шешімі </strong>қолданыста.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Есепті мерзім ішінде, яғни аудандық мәслихаттың 2017 жылғы 22 желтоқсандағы кезектен тыс ХХІ сессиясында &laquo;Қазақстан Республикасындағы жергілікті мемлекеттік басқару және өзін-өзі басқару туралы&raquo;&nbsp; Қазақстан Республикасының Заңының 6-бабының 1 тармағы 12-1) тармақшасына сәйкес және аудан әкімінің ұсынысының негізінде Қазақстан Республикасы Тәуелсіздігінің 27 жылдығына орай ауданымыздың қоғамдық өміріне белсенді араласып, әлеуметтік-экономикалық тұрғыдан дамуына қосқан елеулі үлесі үшін <strong>10 азаматқа &laquo;Еңбекшіқазақ ауданының құрметті азаматы&raquo; </strong>атағы берілді.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Депутаттардың сессия отырысына қатысу үлесі <strong>93 пайызды құрап отыр.</strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>3. МӘСЛИХАТТЫҢ ТҰРАҚТЫ КОМИССИЯЛАРЫ </strong></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&laquo;Қазақстан Республикасындағы жергілікті мемлекеттік басқару және өзін-өзі басқару туралы&raquo; Қазақстан Республикасы Заңының <strong>12-бабы 1-тармағына</strong> сәйкес мәслихат өзінің өкілеттік мерзіміне <strong>4 тұрақты</strong> комиссия құрды:</p>\r\n\r\n<p style=\"text-align:justify\"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 1)</strong> &laquo;Әлеуметтік салаларды дамыту мен заңдылық, құқықтық тәртіп, қоғамдық келісім, жергілікті мемлекеттік және өзін &ndash; өзі басқару жөніндегі&raquo; тұрақты комиссия. Құрамына жалпы 6 депутат бекітілген. Төрайымы Егенбаев Гульсара Калиевна.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>2)</strong> &laquo;Экономика, бюджет және қаржы мәселелері жөніндегі&raquo; тұрақты комиссия. Құрамында 5 депутат. Төрағасы Байғоджаев Қуат Сұлтанович.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>3)</strong> &laquo;Агроөнеркәсіп, шағын&nbsp; және орта бизнес пен іскерлік белсенділікті қолдау, индустриалды -инновациялық даму мәселелері жөніндегі&raquo; тұрақты комиссия. Құрамында 6 депутат бар. Төрағасы Рустамбеков Айдар Омарханович.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>4)</strong> &laquo;Энергетика мен жалпы инфрақұрылымды дамыту және тұрғын үй-коммуналдық шаруашылық мәселелері жөніндегі&raquo; тұрақты комиссия. Құрамында 5 депутат бекітілген. Төрағасы Қырқынбаев Сейтхан Есенбайұлы.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Жалпы 2018 жылы тұрақты комиссиялардың&nbsp; <strong>12 отырысы</strong> (оның ішінде 4 көшпелі, 6 біріккен және 2 жеке) өткізілді. Онда <strong>46 есеп</strong> тыңдалды. Қабылданған қаулылардың орындалу үлесі <strong>78,3 пайыз</strong>.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Депутаттардың тұрақты комиссия отырысына қатысу үлесі <strong>91,7 пайыз</strong></p>\r\n\r\n<p style=\"text-align:center\"><strong>4. ДЕПУТАТТЫҚ КОРПУСТЫҢ&nbsp; ЖАЛПЫ ЖҰМЫСЫ</strong></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 1. Депутаттардың жұмысындағы ең&nbsp; маңыздысы, олардың сайлау округтерінде орындаған тиімді жұмыстары болып табылады. Сондықтан аудандық мәслихат депутаттары депутаттық өкілеттіктерін жүзеге асыру мақсатында өздері сайланған округтер мен қызмет орындарында&nbsp; және &laquo;Нұр Отан&raquo; партиясы аудандық филиалы ғимаратында белгіленген кестеге сәйкес сайлаушыларды қабылдайды. Қабылдау мерзімі ауданның бұқаралық ақпарат құралдары беттерінде жарияланып, сайлау учаскелерінде қабылдау кестесі жасалып ілінген.&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2. Аудандық мәслихаттағы &laquo;Нұр Отан&raquo; депутаттық фракциясы. Депутаттардың көпшілігі, &laquo;Нұр Отан&raquo; партиясының мүшелері (22 адам). Сондықтан, фракцияның қызметін ұйымдастыру аудандық партия филиалы мен тікелей төрағаның бірінші орынбасары Байғоджаев Қуат Сұлтанұлына бекітілген.&nbsp; Ал, &laquo;Ауыл&raquo; ХДПП аудандық филиалынан 1 адам сайланды.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 3. &laquo;Қазақстан Республикасындағы жергілікті мемлекеттік басқару және өзін-өзі басқару туралы&raquo; Заңының 9-бабына сәйкес, аудандық мәслихат хатшысы мен депутаттары&nbsp; аудандағы сайлау округтерінде 2018 жылы <strong>23</strong> <strong>есеп беру</strong> <strong>кездесулері</strong> өткізілді.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp; &nbsp; &nbsp; &nbsp;4. Депутаттық қызметті жүзеге асыру шеңберінде үлкен, әр қырынан келуді талап ететін мәселелер бойынша біздің тарапымыздан <strong>депутаттық сауал</strong> жолдау құқығы барынша пайдаланылып келеді. Есепті кезеңде&nbsp; түрлі бағытта <strong>18</strong> депутаттық сауал жолданды.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 5. Тұрақты ақпараттық жұмыс тобы 25 ауылдық округте және 1 қалада әлеуметтік және стратегиялық маңызды нысандарды аралап көріп, кездесулер өткізді.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Аталған кездесулердің барысында 75 елді мекен мен 1 қала қамтылып, тиісінше округ активінің, ұжымдардың, ауыл тұрғындары мен жергілікті қоғамдастық тарапынан бірқатар маңызды да, түйіткілді мәселелер көтерілді. Жалпы <strong>356 сұраныс</strong> түсті, олар тиісінше маңыздылығы бойынша сарапталып <strong>204 мәселеге</strong> топтастырылды. Қалған <strong>152 сұраққа</strong> тікелей сол жерде бірден ауызша жауап берілді.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp; &nbsp; &nbsp; &nbsp;6. Аудандық мәслихат депутаттары &ndash; Елбасы саясатының белсенді таратушылары&nbsp; және аудан көлемінде жүзеге асырылып жатқан игілікті іс-шараларға белсене араласып, ауданның атқарушы және жергілікті қоғамдық ұйымдармен бірлесе отырып өзіндік үлестерін қосуда.Аудандық мәслихаттың ақпараттық жұмыс тобының мүшелері ретінде олар есепті кезеңде <strong>120-дан&nbsp; астам</strong> кездесу өткізіп, түсіндіру жұмыстарын жүргізді.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp; &nbsp; &nbsp;7. Сондай-ақ, депутаттардың сайлауалды бағдарламаларының жүзеге асырылуы мен орындалуы аудандық мәслихат аппараттының үнемі назарында. Барлық депутаттардың сайлау алды бағдаламамаларында көзделген негізгі бағыттар бойынша жұмыс жүргізілуде, оның 69,5 %-ы орындалды.</p>\r\n\r\n<p style=\"text-align:center\"><strong>5. АҚПАРАТТЫҚ ҚОЛДАУ. </strong></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp; &nbsp; &nbsp; Депутаттардың белсенділігінің тиімділігін көтеретін формалардың бірі, олардың жергілікті баспасөз қызметімен тұрақты байланыста болуы. Ағымдағы жылы аудандық, облыстық, республикалық бұқаралық ақпарат құралдарының басылымдарында жалпы <strong>190 мақала жарық көрді;</strong>&nbsp;ғаламтордағы көптілді халықаралық сайт ВКонтакте немесе VK әлеуметтік желісінде https://vk.com/enbekshikazakh.maslihat адресі бойынша Еңбекшіқазақ аудандық мәслихаты ақпараттық интернет &ndash; ресурс парақшасында <strong>1600-ден астам ақпараттық материалдар</strong> жарияланды.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; . Сондай-ақ, Еңбекшіқазақ аудандық мәслихатының <strong>ресми сайты</strong> ашылып, қолданысқа берілді.</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>6. МӘСЛИХАТ ҚЫЗМЕТІНІҢ ЖАРИЯЛЫЛЫҒЫ</strong></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp; &nbsp; &nbsp; &nbsp; Өкілетті органның қызметі ашық және айқын режимде жүргізіліп, барлық іс-шаралары ашық түрде өткізіледі</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Аудандық мәслихаттың сессия жұмысына аудан әкімі мен оның орынбасарлары, мекемелері, кәсіпорындар мен мемлекеттік органдардың басшылары тұрақты түрде қатысады, сондай-ақ&nbsp; саяси партиялардың, қоғамдық бірлестіктердің, ауданның үкіметтік емес ұйымдарының және бұқаралық ақпарат құралдарының өкілдеріне тұрақты негізде алдын ала хабарланады.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Көшпелі форматты өткізілетін тұрақты комиссия отырыстары мен тыңдауларға, сессияларға тиісті аумақтың активі, жергілікті қауымдастық мүшелері, тұрғындар міндетті түрде шақырылады.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Басты басымдылық жариялылыққа беріледі. Халықтың жедел ақпарат алу үшін аудандық газет пен интернет-ресурстары арқылы сессияның өтетін күні мен сессияның күн тәртібін, кездесулердің кестесі туралы&nbsp; хабарламалар ғана емес, сонымен бірге аудан тұрғындарына&nbsp; дайындық жұмыстарына белсене қатысу туралы үндеу де жариялануда.</p>\r\n\r\n<p style=\"text-align:center\"><strong>Қадірменді көпшілік!</strong></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp; &nbsp; &nbsp; Халықтың қалаулысы болу аса жоғары жауапкершілік екенін біз жақсы түсінеміз. Сондықтан бүгінгі 6-шақырылым мәслихатының барлық бағдарлы қызметі тұрғындардың сенімін ақтап,&nbsp; халық пен билік арасындағы &laquo;алтын көпір&raquo; деген атқа лайықты болуға бағытталып отыр.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp; &nbsp; &nbsp; &nbsp;Атқарылған жұмыс туралы бүгінгі есеп - бұл алтыншы шақырудың өткен кезеңінде аудандық мәслихат жұмысының кейбір қорытындыларын шығару ғана емес, сондай - ақ келешекке жоспарларды белгілеудің жақсы себебіне айналып отыр.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Көп жұмыс атқарылды, бірақ алда да&nbsp; істелінетін істер мол. Жұмысымыздың жағымды жақтарынан басқа, кемшіліктеріміз де бар, оларды біз&nbsp; дер&nbsp; кезінде&nbsp; жетілдіріп және жақсарту бойынша тиісті шаралар&nbsp; қабылдануда.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>&laquo;Ел бірлігі &ndash; еңбек жемісі&raquo;</strong> - деп дана халқымыз айтпақшы, Сөзімді қорытындылай келе алдағы уақытта халықтың сын &ndash; ескертпелеріне, ұсыныс &ndash; пікірлеріне сәйкес нақты жұмыстар атқарылатындығын айтқым келеді.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;Олай болса, баршаңызға зор денсаулық, отбасыларыңызға амандық, еңбектеріңізге толағай табыс, &nbsp;туған еліміздің бағын асыратын, мерейін тасытатын үлкен істе баршаңызға қажымас қайрат пен бақ-береке тілеймін.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Назар қойып тыңдағандарыңызға рахмет!</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img src=\"http://enbekshikazak-maslikhat.kz/admin/uploads/images/94924085_R_RRRRSR.png\" style=\"height:50px; width:150px\" /><img src=\"http://enbekshikazak-maslikhat.kz/admin/uploads/images/94924085_R_RRRRSR.png\" style=\"height:50px; text-align:justify; width:150px\" /><img src=\"http://enbekshikazak-maslikhat.kz/admin/uploads/images/94924085_R_RRRRSR.png\" style=\"height:50px; text-align:justify; width:150px\" /><img src=\"http://enbekshikazak-maslikhat.kz/admin/uploads/images/94924085_R_RRRRSR.png\" style=\"height:50px; text-align:justify; width:150px\" /><img src=\"http://enbekshikazak-maslikhat.kz/admin/uploads/images/94924085_R_RRRRSR.png\" style=\"height:50px; text-align:justify; width:150px\" /></p>\r\n\r\n<p style=\"text-align:center\"><img src=\"http://enbekshikazak-maslikhat.kz/admin/uploads/images/91748206_R13.png\" style=\"height:103px; text-align:center; width:150px\" /></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>Е<strong>ңбекшіқазақ аудандық мәслихаттың 2017 жылы атқарған жұмысы туралы мәслихат хатшысы Б.Ахметовтың есебінің тезистері</strong></em></span></span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp; 2018 жыл, ақпан</em></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><strong><em>Құрметті әріптестер!</em></strong></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><strong><em>Ардақты ағайын!</em></strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Мемлекет басшысының бастамасымен қолға алынған экономикалық және саяси реформалар, мемлекеттік жаңа индустриаландыру бағдарламалары мен өзге де дағдарысқа қарсы шаралар аудан экономикасын дамытуда оң көрсеткіштерге қол жеткізуге жол ашты.</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>&nbsp;&laquo;Еңбегің өнімді болса, көңілің сенімді болар&raquo;</strong> демекші,&nbsp; бүгін 2017 жылды қорытындылай келе, біз толық жауапкершілікпен аудандық депутаттық корпустың назарындағы мәслихат қызметін іске асырумен байланысты бағытты жұмыстар маңызды, іргелі, әрі нәтижелі істермен айшықталып, әлеуметтік-экономикалық маңызды жобалар жүзеге асырылған үлкен жетістіктер мен серпінді дамудың оң көрсеткіштеріне ие болғанын жауапкершілікпен айта аламын. </em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<em>Олай болса, аудандық мәслихаттың регламенті негізінде назарларыңызға мәслихат хатшысының халық алдындағы есебін, яғни аудандық мәслихаттың 2017 жылы атқарған жұмысы туралы&nbsp; есебін ұсынамын.</em></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><strong><em>1. Еңбекшіқазақ аудандық мәслихаттың&nbsp;</em></strong><strong><em>6-шақырылымының сапалық құрамы</em></strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Аудандық Мәслихаттың, оның депутаттарының арқалар жүгі, атқарар міндеті аз емесі белгілі.&nbsp;2016 жылы 20 наурызда өткен саяси науқанда өздеріңіз артқан сенімді арқалап, жергілікті өкілді билік органы болып табылатын аудандық мәслихаттың 6-шақырылымының депутаты атанған <strong>23 азамат</strong> өз өкілеттіктерін жүзеге асыру үшін ілкімді істердің тамырына бойлап, сыбана еңбек етуде.&nbsp;</em><em>23 депутаттың <strong>8-і (34,8%) бұрын</strong> аудандық мәслихаттың әр түрлі шақырылымдарында сайланған, олардың&nbsp; жылдар бойғы жинақталған тәжірибесі депутаттық корпус қызметінің айтарлықтай тиімділігін арттыруда.</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Жалпы Еңбекшіқазақ аудандық мәслихаттың 6 - шақырылымының құрамы <strong>65,2 пайызға жаңартылды</strong>, 20 депутаттың жоғарғы (86,9%), 3 депутаттың орта арнаулы (13,1%) білімі бар.&nbsp;Депутаттық корпустың <strong>47,8 %-ын</strong> (11 адам) ауданымыздың экономикасына үлес қосып жүрген кәсіпкерлер, <strong>30,4%-ын</strong> (6 адам), мемлекеттік мекеме басшылары, <strong>8,7 %-ын</strong> (2 адам) мекеме басшыларының орынбасарлары, <strong>13% -ын</strong> (3 адам) өзге сала өкілдері құрайды.&nbsp;<strong>Гендерлік құрамы</strong>&nbsp; бойынша 82,6 пайызы (19) ерлер, 17,4 пайызы (4) әйел азаматшалардан тұрады. &nbsp;<strong>Ұлттық құрамы</strong> бойынша: қазақтар -17 (73,9%), ал өзге ұлт өкілдерінің үлесі 26,1 % &ndash; яғни 6 адам.</em></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><strong><em>Құрметті ағайын!</em></strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Дана халқымыздың &laquo;<strong>Адамның өскені әрбір өткен жылымен, шаһардың өскені әз-халықтың санымен бағаланады&raquo;</strong> деген сөзіне сай, өткенді саралап, алдыға ой салып, мәслихаттың бағындырған межелерін бағамдап алайық.</em></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><strong><em>2. МӘСЛИХАТ СЕССИЯСЫ</em></strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Заңға сәйкес мәслихаттардың қызметінің негізгі нысаны <strong>сессия</strong> болып табылады және дәл сессия арқылы біз атқарушы органдармен бірлесе отырып, Елбасымен белгіленген міндеттердің, бағдарламалардың іске асырырылуы мен орындалуын қарастырып, көзделген межелерге қол жеткізе аламыз. </em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Сессияларға депутаттардың орташа қатысуы қазір <strong>86,9%</strong> құрап отыр. </em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2017 жылы аудандық мәслихат өз өкілеттілігінің шегінде <strong>12 сессия</strong> (2016 жылы - 11, жалпы 23) өткізіп, <strong>53 шешім</strong>&nbsp; (2016 жылы - 59, жалпы 112 шешім) қабылдады.</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">&nbsp;&nbsp;&nbsp;<em>Сессиялық қызмет барысында бюджетті қалыптастыру, оның орындалуы, өзгерістер мен қосымшалар енгізу мәселелеріне көп көңіл бөлінді. Есепті кезеңде </em><em>осы бағытта - <strong>7 шешім</strong> қарастырылған. </em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Қазақстан Республикасы Президентінің 2006 жылғы 18-қаңтардағы № 19 &laquo;Әкімдердің мәслихаттар алдында есеп беруін өткізу туралы&raquo; Жарлығына сәйкес 1 рет аудан <strong>әкімінің, 3 рет ауылдық округ</strong> (Балтабай, Түрген, Асы) әкімдерінің <strong>есебі</strong> тыңдалды.</em><em> </em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp; &nbsp; Сондай-ақ, Алматы облысы бойынша тексеру комиссиясының аудандық бюджеттің игерілуі мен &nbsp;орындалуы туралы&nbsp; есебі <strong>1 рет</strong>, жергілікті полиция қызметі бастығының есебі <strong>2 рет</strong>, ауданымыздың саяси, экономикалық, әлеуметтік, мәдени дамуына негізделген тиісті салалық бағдарламалардың орындалуы туралы аудан әкімінің орынбасарларының, мекеме, бөлім, басқарма басшыларының есептері <strong>6 рет </strong>қаралды. </em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Бұдан бөлек аудан әкімінің ұсыныс хатына сәйкес аудандық мәслихаттың өкілеттігіне жататын өзге де бағыттар да бар. </em><em>Олардың қатарында ономастика бойынша <strong>9 бірлескен қаулы мен шешім</strong>, Еңбекшіқазақ ауданының 2016-2020 жылдарға арналған даму бағдарламасына өзгерістер енгізу туралы <strong>2</strong>, ведомствоаралық сипаттағы мәселелер бойынша әкімдік жанындағы консультациялық-кеңесші органдардың дербес құрамын бекіту және өзгертулер енгізу туралы <strong>7 </strong>және өзге де әртүрлі маңызды мәселелерді қарастыра отырып <strong>16 шешім</strong> қабылданды.</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp; &laquo;Нормативтік құқықтық актілер туралы&raquo; Қазақстан Республикасының Заңына сәйкес, құқықтық нормаларын&nbsp; белгілейтін,&nbsp; сессияның <strong>15 шешімі</strong> (2016 жылы - 8, жалпы 23) әділет органдарында мемлекеттік тіркеуді өтті және мемлекеттік нөмірі беріліп, Республикалық құқықтық ақпарат орталығы арқылы нормативтік құқықтық актілерінің Мемлекеттік тізіліміне, Қазақстан Республикасы нормативтік құқықтық актілерінің Эталондық бақылау банкінің жүйесіне енгізілді. &nbsp;&nbsp;&nbsp;&nbsp; </em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">&nbsp;<em>Заң талабына сәйкес <strong>8 (сегіз)</strong> нормативтік құқықтық акті ретінде тіркелген шешімнің күші жойылып, қолданыстан шығарылды. Жалпы бүгінгі таңда қолданыста мәслихат тарапынан қабылданған <strong>28 нормативтік құқықтық акті бар</strong>.</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp; Есепті мерзім ішінде, яғни аудандық мәслихаттың 2017 жылғы 22 желтоқсандағы кезектен тыс ХХІ сессиясында &laquo;Қазақстан Республикасындағы жергілікті мемлекеттік басқару және өзін-өзі басқару туралы&raquo; Қазақстан Республикасының Заңының 6-бабының 1 тармағы&nbsp;12-1) тармақшасына сәйкес және аудан әкімінің ұсынысының негізінде Қазақстан Республикасы Тәуелсіздігінің 26 жылдығына орай ауданымыздың қоғамдық өміріне белсенді араласып, әлеуметтік-экономикалық тұрғыдан дамуына қосқан елеулі үлесі үшін <strong>10 азаматқа &laquo;Еңбекшіқазақ ауданының құрметті азаматы&raquo; </strong>атағы берілді.</em></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>3. Мәслихаттың тұрақты комиссиялары </strong></em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&laquo;Қазақстан Республикасындағы жергілікті мемлекеттік басқару және өзін-өзі басқару туралы&raquo; Қазақстан Республикасы Заңының&nbsp;<strong>12-бабы 1-тармағына</strong> сәйкес мәслихат өзінің өкілеттік мерзіміне <strong>4 тұрақты</strong> комиссия құрды:</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong><em>&nbsp; &nbsp;1)</em></strong><em> &laquo;Әлеуметтік салаларды дамыту мен заңдылық, құқықтық тәртіп, қоғамдық келісім, жергілікті мемлекеттік және өзін &ndash; өзі басқару жөніндегі&raquo; тұрақты комиссия. Құрамына жалпы 6 депутат бекітілген. Төрайымы Егенбаев Гульсара Калиевна. Есепті кезеңде комиссияның бағытына сәйкес жалпы <strong>9 сала</strong> бойынша<strong> 11 есеп</strong> тыңдалып, оның <strong>1-уі</strong> қанағаттандырылмады. Ол аудандық білім бөлімі.</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>2)</strong> &laquo;Экономика, бюджет және қаржы мәселелері жөніндегі&raquo; тұрақты комиссия. Құрамында 5 депутат. Төрағасы Байғоджаев Қуат Сұлтанович. Комиссияның бағытына сәйкес <strong>3 сала</strong> бойынша <strong>10 есеп</strong> қаралды.</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<em><strong>3)</strong> &laquo;Агроөнеркәсіп, шағын&nbsp; және орта бизнес пен іскерлік белсенділікті қолдау, индустриалды -инновациялық даму мәселелері жөніндегі&raquo; тұрақты комиссия. Құрамында 6 депутат бар. Төрағасы Рустамбеков Айдар Омарханович. Комиссияның бағыты бойынша 5<strong> бөлімнің</strong> қызметіне сәйкес <strong>9 есеп</strong> қаралды.</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;<strong>4)</strong> &laquo;Энергетика мен жалпы инфрақұрылымды дамыту және тұрғын үй-коммуналдық шаруашылық мәселелері жөніндегі&raquo; тұрақты комиссия. Құрамында 5 депутат бекітілген. Төрағасы Қырқынбаев Сейтхан Есенбайұлы. Негізгі бағытына сәйкес <strong>6 бөлімнің</strong> қызметі бойынша<strong> 10 есеп</strong> қаралды.</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">&nbsp; &nbsp;&nbsp;<em>Әрбір комиссия тек бір ғана бағытта кешенді түрде жұмыс жасайды. Олардың міндеті &ndash; сол ауқымдағы салаларды толық қамти отырып, нақты түйіткілді мәселелердің шешімін табуды жолға қою.&nbsp;&nbsp; </em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<em>&nbsp;Аталған комиссиялардың отырыстарында сессиялардың қарауына енгізілетін мәселелерді алдын ала қарау, мәслихат шешімдерінің&nbsp; іске асуына және бақылау функцияларын жүзеге асыру ғана емес, жергілікті атқарушы органдардың жұмысының тиімділігі мен олардың деңгейін анықтауға бағытталған есептері сараланып, мониторингтен өткізілді. </em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<em>Бұл бағытта 2017 жылы <strong>4 көшпелі</strong> кеңейтілген көпшілік тыңдау отырысы өткізіліп, Есік қаласы мен ауылдық округтердің хабарламалары талданды.</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<em>&nbsp;Сондай-ақ, ауданымыздан сайланған облыстық мәслихат депутаттары өздерінің келісімдері бойынша жаңадан құрылған тұрақты комиссияларға қосымша бақылаушы ретінде бекітілді. </em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Сондықтанда комиссияларда көтерілген әрбір мәселені шешу күрделілігіне қарай аудан деңгейінде ғана емес, облыс деңгейінде де депуттаттар тарапынан сауалдар жолдануда.</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Жалпы 2017 жылы тұрақты комиссиялардың&nbsp;<strong>10 отырысы</strong> (оның ішінде 4 көшпелі, 5 біріккен және 1 жеке) өткізілді. Онда <strong>40 мәселе</strong> қарастырылып<strong>, 62 есеп</strong> тыңдалды. Қабылданған қаулылардың орындалу үлесі <strong>72,7 пайыз</strong>.</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">&nbsp;&nbsp;&nbsp;<em>Комиссия отырыстарындағы ашықтық, депутат пен тұрғындар арасындағы тығыз байланыс, көшпелі комиссия отырыстары бәрі үлкен ізденіс пен толымды тәжірибенің тоғысуынантуындап отырған өзгерістер.</em></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><strong><em>4. Депутаттық корпустың жұмысы</em></strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 1. Депутаттардың жұмысындағы ең&nbsp; маңыздысы, олардың <strong>сайлау округтерінде</strong> орындаған тиімді жұмыстары болып табылады.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Сондықтан аудандық мәслихат депутаттары депутаттық өкілеттіктерін жүзеге асыру мақсатында өздері сайланған округтер мен қызмет орындарында&nbsp; және &laquo;Нұр Отан&raquo; партиясы аудандық филиалы ғимаратында белгіленген кестеге сәйкес сайлаушыларды қабылдайды. Қабылдау мерзімі ауданның бұқаралық ақпарат құралдары беттерінде жарияланып, сайлау учаскелерінде қабылдау кестесі жасалып ілінген.&nbsp; </em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">&nbsp; &nbsp;&nbsp;<em>Есепті мерзім ішінде депутаттардың сайлаушыларды қабылдауы барысында барлығы <strong>133</strong>&nbsp; <strong>өтініш </strong>түсіп, олар депутаттар арқылы аудандық мәслихат сессияларында, тұрақты комиссиялар отырыстарында, депутаттық сауалдар арқылы шешімін табуда. Сайлаушылардың ұсыныс-пікірлері мен талап тілектерінің <strong>66,9 пайызы </strong>немесе<strong> 78-і </strong>орындалды.</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Аудандық мәслихат депутаттарының жыл сайын ашық есік күнін өткізу дәстүрге айналды. 2017 жылы бұл бағыттағы <strong>6 </strong>шара өткізілді. Сондай-ақ, депутаттардың сайлауалды бағдарламаларының жүзеге асырылуы мен орындалуы аудандық мәслихат аппараттының үнемі назарында.</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp; &nbsp;2. <strong>Аудандық мәслихаттағы &laquo;Нұр Отан&raquo; депутаттық фракциясы</strong>. Депутаттардың көпшілігі, &laquo;Нұр Отан&raquo; партиясының мүшелері (22 адам). Сондықтан, фракцияның қызметін ұйымдастыру аудандық партия филиалы мен тікелей төрағаның бірінші орынбасары Байғоджаев Қуат Сұлтанұлына бекітілген.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2017 жылы &laquo;Нұр Отан&raquo; партиясы аудандық филиалында <strong>132 қабылдау</strong> өткізіліп, <strong>118 ұсыныс</strong> түскен, оның <strong>45-і </strong>шешімін тапты. </em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp; &nbsp; &nbsp;Тоқтала кететін жайт, &laquo;Нұр Отан&raquo; партиясының Қоғамдық қабылдау бөлмелері жұртшылықпен арадағы үздіксіз байланысты қамтамасыз ететін бірден-бір көпір болып табылады. </em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Сондықтан, &laquo;Нұр Отан&raquo; қабылдау бөлмелерінің жұмысының тиімділігі мен жүйелілігі және жұмыс тәсілін оңтайлы ұйымдастыру сипаты басты назарға алынуы қажет. </em></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>3. Есептік кездесулер, ақпараттық жұмыс және депутаттық сауал. </strong></em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>&laquo;Қазақстан Республикасындағы жергілікті мемлекеттік басқару және өзін-өзі басқару туралы&raquo; Заңының 9-бабына сәйкес, аудандық мәслихат хатшысы мен депутаттары&nbsp; аудандағы сайлау округтерінде 2017 жылы <strong>23 есеп беру кездесулері</strong> өткізіліп, <strong>1175 адам қатысып</strong>, <strong>182 сұрақ</strong> келіп түсті. Оның&nbsp; <strong>117-не сол</strong> жерде жауап беріліп, ал қалған сұрақтарға түсінік беру үшін, сәйкес бағытта жұмыс істейтін органдарға жіберіліп, тұрақты бақылауға алынған.</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Бұдан бөлек 2017 жылы барлығы <strong>52 елді мекенді </strong>аралап, Есік қаласы мен 25 ауылдық округте 26 кездесу өткізілді. Кездесулердің барысында тұрғындар тарапынан (жеке сұрақтарды қоспағанда) түскен<strong> 128 </strong>ұсыныс &ndash; пікірлердің іс-шарасы жасалып, орындалуы үшін тиісті органдарға жолданды және орындалуы тұрақты бақылауға алынды<strong>.&nbsp; </strong></em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<em>Нәтижесінде айтылған ұсыныс-пікірлердің&nbsp; 32-сі толық шешімін тауып, 47-і&nbsp; алдағы жылдардың жоспарларына енгізілген және жоспарға енгізуге ұсыныс жасалған, 49-на тиісті заңнама талаптарына сәйкес түсіндіру жұмыстары&nbsp; жүргізілді.</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;Депутаттардың белсенділігінің тиімділігін көтеретін формалардың бірі, олардың жергілікті баспасөз қызметімен тұрақты байланыста болуы. Ағымдағы жылы аудандық, облыстық, республикалық бұқаралық ақпарат құралдарының басылымдарында жалпы <strong>130-дан астам</strong> ақпараттық материалдар, оның ішінде&nbsp;<strong>13 </strong>сұхбат, <strong>11 </strong>мәслихат аппаратының ақпараты, <strong>19 </strong>сессия мен тұрақты комиссия туралы, <strong>56 </strong>депутаттық мақалалар жарияланды.</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp; &nbsp; &nbsp;Сондай-ақ, мәслихат депутаты ресми жазбаша сауалмен әкiмге, тиiстi аумақтық сайлау комиссиясының төрағасына және мүшесiне, прокурорға және орталық мемлекеттiк органдардың аумақтық бөлiмшелерiнiң, жергiлiктi бюджеттен қаржыландырылатын атқарушы органдардың лауазымды адамдарына мәслихат құзыретiне жатқызылған мәселелер бойынша жүгiнуге құқылы.</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<em>&nbsp; Сондықтан, депутаттық қызметті жүзеге асыру шеңберінде үлкен, әр қырынан келуді талап ететін мәселелер бойынша біздің тарапымыздан депутаттық сауал жолдау құқығы барынша пайдаланылып келеді. Есепті кезеңде&nbsp; түрлі бағытта <strong>11 депутаттық сауал</strong> жолданды. <strong>Дегенмен,</strong> жауапты мекемелер мен лауазымды тұлғалардың тарапынан депутаттық сауалға жауап беру кезінде салғырттық таныту жиі орын алуда.</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp; &nbsp; &nbsp;Сол себепті, депутаттық сауалдың орындалуына бақылау қоюдың бірден бір тиімді түрі, олардың атқарған жұмыстары туралы есептерін тыңдау мен тиісті жауапкершілікке тарту туралы шаралар қолдану болып табылады. Бұл ағымдағы 2018 жылы өз нәтижесін беретіндігі сөзсіз. Бұл тұрғыдан атқарушы органдар қажетті ақпаратты ұсынар алдында қандай да бір мәселені алдын ала, егжей-тегжейлі зерделеулері тиіс екендігін ескертемін.</em></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </em><strong><em>5. Мәслихат қызметінің жариялылығы</em></strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Өкілетті органның қызметі ашық және айқын режимде жүргізіліп, барлық іс-шаралары ашық түрде өткізіледі. </em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<em>&nbsp;Аудандық мәслихаттың сессия жұмысына аудан әкімі мен оның орынбасарлары, мекемелері, кәсіпорындар мен мемлекеттік органдардың басшылары тұрақты түрде қатысады, сондай-ақ&nbsp; саяси партиялардың, қоғамдық бірлестіктердің, ауданның үкіметтік емес ұйымдарының және бұқаралық ақпарат құралдарының өкілдеріне тұрақты негізде алдын ала хабарланады.</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">&nbsp;&nbsp;&nbsp;<em>&nbsp;Көшпелі форматты өткізілетін тұрақты комиссия отырыстары мен тыңдауларға, сессияларға тиісті аумақтың активі, жергілікті қауымдастық мүшелері, тұрғындар міндетті түрде шақырылады.</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp; &nbsp; Басты басымдылық жариялылыққа беріледі. Халықтың жедел ақпарат алу үшін аудандық газет пен интернет-ресурстары арқылы сессияның өтетін күні мен сессияның күн тәртібін, кездесулердің кестесі туралы&nbsp; хабарламалар ғана емес, сонымен бірге аудан тұрғындарына&nbsp; дайындық жұмыстарына белсене қатысу туралы үндеу де жариялануда. </em></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><strong><em>6. Мемлекеттік бағдарламаларды ақпараттық қолдау</em></strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Аудандық мәслихат депутаттары &ndash; Елбасы саясатының белсенді таратушылары&nbsp; және аудан көлемінде жүзеге асырылып жатқан игілікті іс-шараларға белсене араласып, ауданның атқарушы және жергілікті қоғамдық ұйымдармен бірлесе отырып, ел алдына ауқымды міндеттер қойған Қазақстан Республикасы Президентінің Қазақстан халқына арнаған Жолдауының, &laquo;Бес институционалдық реформаны жүзеге асыру бойынша 100 нақты қадам&raquo; &ndash; Ұлт жоспарының, &laquo;Рухани жаңғыру&raquo;, &laquo;Жұмыспен қамту - 2020&raquo;, &laquo;Дипломмен - ауылға&raquo;, &laquo;Бизнестің жол қартасы 2020&raquo; және басқа да стратегиялық сипаттағы бағдарламалық құжаттардың жүзеге асырылуына өзіндік үлестерін қосуда.</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp; &nbsp; &nbsp;Аудандық мәслихаттың ақпараттық жұмыс тобының мүшелері ретінде олар есепті кезеңде <strong>30-дан астам</strong> кездесу өткізіп, түсіндіру жұмыстарын жүргізді.</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Жыл ішінде депутаттар тарапынан <strong>8,2 млн. теңге</strong> &nbsp;мөлшерінде өз қаражаттарынан аудан тұрғындарына көмек көрсетілді.</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Бұдан бөлек депутаттар әкімдердің халықпен кездесуіне белсенді қатысушы, атқарушы органдардың кеңестері мен консультативтік - кеңесші органдарының мүшесі болып табылады. Бұның бәрі өзара ақпарат алысуды жақсартуға, туындаған проблемалар бойынша шешімдер табуға мүмкіндік берді.</em></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><strong><em>7. САЙЛАУ</em></strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp; &nbsp; &nbsp;- 2017 жылғы 12 шілдеде өткізілген Еңбекшіқазақ ауданы Балтабай ауылдық округінің әкімінің сайлауына 22 - депутаттың 21 қатысып, сайлауға қатысушы 4 үміткердің ішінен, Мықыбаев Жұман Сембайұлы сайланды.</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - &nbsp;Еңбекшіқазақ ауданы бойынша Есік қаласы, Ақши, Асы, Бартоғай, Бәйдібек би, Бәйтерек, Көктөбе, Қазақстан, Қаражота, Қаракемер, Қаратұрық, Масақ, Рахат, Ташкенсаз, Тескенсу, Түрген ауылдық округтері әкімдерін сайлау 2017 жылғы 8 тамызында&nbsp; өтіп, 48 үміткердің 16-сы сайланды.</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - 2017 жылғы 08 қарашада өткізілген Еңбекшіқазақ ауданы Саймасай ауылдық округінің әкімін сайлауға 23 - депутаттың 21 қатысып, сайлауға қатысушы 3 үміткердің ішінен, Мейрамқұлов Мухамед &ndash; Али Сәбитұлы сайланды.</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>2019 жылғы 18 сәуірде</strong> Еңбекшіқазақ аудандық аумақтық, округтік және учаскелік сайлау комиссияларының мүшелерінің (2014 жылғы 18 сәуірдегі 31 сессияда сайланған) &laquo;Қазақстан Республикасындағы сайлау туралы&raquo; Конституциялық Заңының 10 бабының&nbsp; 3-тармағына сәйкес өкілеттiк мерзiмi (5 жыл) аяқталатындығын назарларыңызға саламын. </em></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 8. Мәслихат аппараты.</em></strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;Аудандық мәслихат аппаратында мемлекеттік органдармен, лауазымды&nbsp; адамдармен ақпарат алмасу &laquo;Әкімшілік рәсімдер туралы&raquo; Қазақстан Республикасының Заңында белгіленген тәртіппен жүзеге асырылады. </em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Есепті мерзім ішінде аудандық мәслихатқа жеке азаматтардан <strong>4 арыз бен өтініш</strong> түсті. Түскен өтініштер тұрғын үй, жер учаскесін сұрау, балабақшалардағы балаларды тамақтандыру талаптарын сақтау, жеке отбасылық мәселеге байланысты. </em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<em>&nbsp; Арыз, өтініш иелерінің талап-тілектері &laquo;Жеке және заңды тұлғалардың өтініштерін қарау тәртібі туралы&raquo; ҚР Заңына сәйкес орындаушы органдарға немесе арыз, өтініште көрсетілген мәселелерді шешу құзіретіне кіретін субъектіге немесе лауазымды тұлғаға жолданып, жауабының тиісті мерзімде&nbsp; қайтарылуы бақылауға алынды.</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Мәслихат хатшысы ретінде мен күн сайын сайлаушылардың жеке сауалдары бойынша қабылдау жүргіземін. Есеп беру кезеңінде <strong>175 </strong>адам қабылдауда болып, <strong>133</strong> сауал қойылып, барлығына жауап берілді. </em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Аудандық мәслихат&nbsp; аппаратында қызметтік құжаттардың қаралауы мен өтуі,&nbsp; ақпарат алмасулар тиісті заң талабына сәйкес жүзеге асырылады. Есепті мерзім ішінде аудандық мәслихатқа барлығы <strong>148 құжат</strong> кірістеліп, <strong>214 құжат</strong> шығысталды. </em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Бүгінгі таңда аппаратта <strong>5&nbsp; мемлекеттік</strong> <strong>қызметкер</strong> бар. &nbsp;Олардың барлығы 2017 жылдың бірінші жартыжылдығында өткен &laquo;Б&raquo; корпусы мемлекеттік қызметшілері атқаратын қызметінің санатына байланысты үш кезеңнен тұратын кешенді аттестаттауға қатысқан болатын. </em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;Бұл тұрғыдан аудандық мәслихат аппаратының мемлекеттік қызметшілері тестілеу барысында да, әңгімелесу барысында да жақсы нәтижелер көрсетіп, аттестаттаудан сәтті өтті.</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">&nbsp;&nbsp;<em>Мамандардың әрқайсысы тұрақты комиссиялар құрамындағы депутаттарға өз өкілеттіктерін жүзеге асыруына көмек көрсетеді. Сессиялар мен тұрақты комиссиялар отырыстарының&nbsp; құжаттарын әзірлейді. Депутаттардың сайлау алды бағдарламаларының орындалуын, депутат өкілеттігінің жүзеге асырылуын&nbsp; назарда ұстайды.</em></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><strong><em>Қадірменді көпшілік!</em></strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">&nbsp;&nbsp;<em>Елбасы Н.Ә.Назарбаевтың &nbsp;&laquo;Жаңа саяси жүйеде мәслихаттардың табиғаты өзгереді.Олар атқарушы билікке қосалқы болмайды. Азаматтық қоғамның сұраныстарын&nbsp; алға жетелейтін биліктің дербес тармағы болады&raquo; деген. Халықтың қалаулысы болу аса жоғары жауапкершілік екенін біз жақсы түсінеміз. Сондықтан бүгінгі 6-шақырылым мәслихатының барлық бағдарлы қызметі тұрғындардың сенімін ақтап,&nbsp; халық пен билік арасындағы &laquo;алтын көпір&raquo; деген атқа лайықты болуға бағытталып отыр. Атқарылған жұмыс туралы бүгінгі есеп - бұл алтыншы шақырудың өткен кезеңінде аудандық мәслихат жұмысының кейбір қорытындыларын шығару ғана емес, сондай - ақ келешекке жоспарларды белгілеудің жақсы себебіне айналып отыр.</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Көп жұмыс атқарылды, бірақ алда да&nbsp; істелінетін істер мол. Жұмысымыздың жағымды жақтарынан басқа, кемшіліктеріміз де бар, оларды біз&nbsp; дер&nbsp; кезінде&nbsp; жетілдіріп және жақсарту бойынша тиісті шаралар&nbsp; қабылдануда.</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Сөзімді қорытындылай келе алдағы уақытта халықтың сын &ndash; ескертпелеріне, ұсыныс &ndash; пікірлеріне сәйкес нақты жұмыстар атқарылатындығын айтқым келеді.</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp; &nbsp; &nbsp; О</em><em>лай болса, баршаңызға зор денсаулық, отбасыларыңызға амандық, еңбектеріңізге толағай табыс, &nbsp;туған еліміздің бағын асыратын, мерейін тасытатын үлкен істе баршаңызға қажымас қайрат пен бақ-береке тілеймін.</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Назар қойып тыңдағандарыңызға рахмет!</em></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><strong><em>Дайындаған аудандық мәслихат аппаратының бас&nbsp; маманы Б.Сатыбалдиев</em></strong></span></span></p>\r\n\r\n<p style=\"text-align:center\"><img src=\"http://enbekshikazak-maslikhat.kz/admin/uploads/images/94924085_R_RRRRSR.png\" style=\"height:50px; text-align:justify; width:150px\" /><img src=\"http://enbekshikazak-maslikhat.kz/admin/uploads/images/94924085_R_RRRRSR.png\" style=\"height:50px; text-align:justify; width:150px\" /><img src=\"http://enbekshikazak-maslikhat.kz/admin/uploads/images/94924085_R_RRRRSR.png\" style=\"height:50px; text-align:justify; width:150px\" /><img src=\"http://enbekshikazak-maslikhat.kz/admin/uploads/images/94924085_R_RRRRSR.png\" style=\"height:50px; text-align:justify; width:150px\" /><img src=\"http://enbekshikazak-maslikhat.kz/admin/uploads/images/94924085_R_RRRRSR.png\" style=\"height:50px; text-align:justify; width:150px\" /></p>\r\n', '<div style=\"text-align: center;\"><img src=\"http://enbekshikazak-maslikhat.kz/admin/uploads/images/91748206_R13.png\" style=\"height:103px; text-align:center; width:150px\" /></div>\r\n\r\n<div style=\"text-align: center;\"><strong>Тезисы отчета секретаря Енбекшиказахского районного маслихата Б. Ахметова о&nbsp;проделанной работе Енбекшиказахского районного маслихата за 2018 год&nbsp;</strong></div>\r\n\r\n<div style=\"text-align: center;\">&nbsp;</div>\r\n\r\n<div style=\"text-align: right;\"><strong>&nbsp; </strong><em>&nbsp;Февраль 2019 года</em></div>\r\n\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n\r\n<div style=\"text-align: center;\"><em><strong>Уважаемые колеги и жители района!</strong></em></div>\r\n\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n\r\n<div style=\"text-align: justify;\">&nbsp; &nbsp; &nbsp; На основании регламента районного маслихата представляю отчет секретаря маслихата перед населением, то есть отчет о проделанной работе районного маслихата за 2018 год.</div>\r\n\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n\r\n<div style=\"text-align: center;\"><strong>1. Общие положения КАЧЕСТВЕННЫЙ СОСТАВ 6-ГО СОЗЫВА</strong></div>\r\n\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n\r\n<div style=\"text-align: justify;\">&nbsp; &nbsp; &nbsp; &nbsp;23 граждан, ставших депутатом 6-го созыва районного маслихата, ставших местным представительным органом власти, в ходе политической кампании, состоявшейся 20 марта 2016 года, трудятся на благо осуществления своих полномочий.&nbsp;</div>\r\n\r\n<div style=\"text-align: justify;\">&nbsp; &nbsp; &nbsp; &nbsp; 47,8 % депутатского корпуса (11 человек) составляют предприниматели, внесшие вклад в экономику района, 30,4% (6 человек), руководители государственных учреждений, 8,7% (2 человека) заместители руководителей учреждений, 13% (3 человека) представители других сфер.&nbsp;</div>\r\n\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n\r\n<div style=\"text-align: center;\"><strong>2. СЕССИЯ МАСЛИХАТА</strong></div>\r\n\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n\r\n<div style=\"text-align: justify;\">&nbsp; &nbsp; &nbsp; В соответствии с законом основной формой деятельности маслихатов является сессия, и именно через сессию мы можем совместно с исполнительными органами рассмотреть реализацию и реализацию задач, программ, намеченных главой государства.&nbsp;</div>\r\n\r\n<div style=\"text-align: justify;\">&nbsp; &nbsp; &nbsp;Среднее участие депутатов в сессиях сейчас составляет 86,9%.&nbsp;</div>\r\n\r\n<div style=\"text-align: justify;\">&nbsp; &nbsp; &nbsp; В 2018 году районный маслихат в пределах своих полномочий провел 17 сессий и принял 93 решения.</div>\r\n\r\n<div style=\"text-align: justify;\">&nbsp; &nbsp; &nbsp; На сегодняшний день в соответствии с Законом Республики Казахстан&raquo; О нормативных правовых актах &quot; действуют 24 нормативных правовых решения районного маслихата, устанавливающие правовые нормы.</div>\r\n\r\n<div style=\"text-align: justify;\">&nbsp; &nbsp; &nbsp;В отчетном периоде, то есть на внеочередной ХХІ сессии районного маслихата от 22 декабря 2017 года в соответствии с подпунктом 12-1) пункта 1 статьи 6 Закона Республики Казахстан &laquo;О местном государственном управлении и самоуправлении в Республике Казахстан&raquo; и на основании представления акима района в честь 27-летия Независимости Республики Казахстан 10 гражданам было присвоено звание &laquo;Почетный гражданин Енбекшиказахского района&raquo;.</div>\r\n\r\n<div style=\"text-align: justify;\">Доля участия депутатов в заседаниях сессии составляет 93%.</div>\r\n\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n\r\n<div style=\"text-align: center;\"><strong>3. ПОСТОЯННЫЕ КОМИССИИ МАСЛИХАТА&nbsp;</strong></div>\r\n\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n\r\n<div style=\"text-align: justify;\">В соответствии с пунктом 1 статьи 12 Закона Республики Казахстан от 23 января 2001 года&raquo; О местном государственном управлении и самоуправлении в Республике Казахстан &quot; маслихат на срок своих полномочий образовал 4 постоянных комиссии:</div>\r\n\r\n<div style=\"text-align: justify;\">1) Постоянная комиссия &laquo;по развитию социальных сфер и законности, правопорядка, общественного согласия, местного государственного и самоуправления&raquo;. Всего в составе 6 депутатов. Председатель Егенбаев Гульсара Калиевна.</div>\r\n\r\n<div style=\"text-align: justify;\">2) постоянная комиссия &laquo;по вопросам экономики, бюджета и финансов&raquo;. В составе 5 депутатов. Председатель Байгоджаев Куат Султанович.&nbsp;</div>\r\n\r\n<div style=\"text-align: justify;\">3) постоянная комиссия &laquo;по вопросам агропромышленного, малого и среднего бизнеса и поддержки деловой активности, индустриально-инновационного развития&quot;. В составе 6 депутатов. Председатель Рустамбеков Айдар Омарханович.</div>\r\n\r\n<div style=\"text-align: justify;\">4) постоянная комиссия &laquo;по вопросам энергетики и развития общей инфраструктуры и жилищно-коммунального хозяйства&raquo;. В составе 5 депутатов. Председатель Кыркынбаев Сейтхан Есенбаевич.&nbsp;</div>\r\n\r\n<div style=\"text-align: justify;\">В целом в 2018 году проведено 12 заседаний постоянных комиссий (в том числе 4 выездных, 6 совместных и 2 индивидуальных). На нем было заслушано 46 отчетов. Доля исполнения принятых постановлений составляет 78,3%.</div>\r\n\r\n<div style=\"text-align: justify;\">Доля участия депутатов в заседаниях постоянных комиссий 91,7%</div>\r\n\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n\r\n<div style=\"text-align: center;\"><strong>4. ОБЩАЯ РАБОТА ДЕПУТАТСКОГО КОРПУСА</strong></div>\r\n\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n\r\n<div style=\"text-align: justify;\">1. Общие положения Важным в работе депутатов является эффективная работа, которую они выполняли в избирательных округах. Поэтому депутаты районного маслихата в целях осуществления депутатских полномочий принимают избирателей в округах и местах их избрания и в здании районного филиала партии &laquo;Нур Отан&raquo; в соответствии с установленным графиком. Сроки приема были опубликованы в средствах массовой информации района, на избирательных участках составлен график приема.&nbsp;&nbsp;</div>\r\n\r\n<div style=\"text-align: justify;\">2. Депутатская фракция &laquo;Нур Отан&raquo; в районном маслихате. Большинство депутатов, члены партии&raquo; Нур Отан &quot; (22 человека). Поэтому организация деятельности фракции закреплена за районным филиалом партии и первым заместителем председателя Байгоджаевым Куатом Султановым.&nbsp; Из районного филиала СДПП&raquo; Ауыл &quot; избран 1 человек.</div>\r\n\r\n<div style=\"text-align: justify;\">3. В соответствии со статьей 9 Закона Республики Казахстан&raquo; О местном государственном управлении и самоуправлении в Республике Казахстан &quot; секретарем и депутатами районного маслихата в избирательных округах района в 2018 году проведено 23 отчетных встреч.&nbsp;</div>\r\n\r\n<div style=\"text-align: justify;\">4. В рамках осуществления депутатской деятельности максимально используется право на обращение депутатских запросов с нашей стороны по большому, требующему от каждого из сторон. За отчетный период по различным направлениям направлено 18 депутатских запросов.&nbsp;</div>\r\n\r\n<div style=\"text-align: justify;\">5. Постоянная информационная рабочая группа посетила 25 сельских округов и 1 города, где осмотрели объекты социально-экономического и стратегического назначения.</div>\r\n\r\n<div style=\"text-align: justify;\">В ходе данных встреч были затронуты 75 населенных пунктов и 1 город, соответственно были подняты ряд важных и проблемных вопросов со стороны актива округа, коллективов, жителей села и местного сообщества. В целом поступило 356 запросов, которые были проанализированы по соответствующей значимости и сгруппированы на 204 вопроса. На остальные 152 вопроса непосредственно на месте были даны устные ответы.</div>\r\n\r\n<div style=\"text-align: justify;\">6. Депутаты районного маслихата-активные распространители политики Главы государства и принимают активное участие в проводимых на территории района благотворительных мероприятиях, вносят свой вклад совместно с местными исполнительными и общественными организациями района.В качестве членов информационной рабочей группы районного маслихата за отчетный период проведено более 120 встреч и разъяснительная работа.</div>\r\n\r\n<div style=\"text-align: justify;\">7. Также в центре внимания аппарата районного маслихата находится реализация и выполнение предвыборной программы депутатов. Ведется работа по основным направлениям, предусмотренным в предвыборных программах всех депутатов, 69,5% из них исполнено.</div>\r\n\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n\r\n<div style=\"text-align: center;\"><strong>5. ИНФОРМАЦИОННАЯ ПОДДЕРЖКА.&nbsp;</strong></div>\r\n\r\n<div style=\"text-align: justify;\">Одной из форм повышения эффективности активности депутатов является постоянный контакт с местной пресс-службой. В текущем году в районных, областных, республиканских средствах массовой информации опубликовано 190 статей.;</div>\r\n\r\n<div style=\"text-align: justify;\">в интернете многоязычный международный сайт ВКонтакте или в социальной сети VK https://vk.com/enbekshikazakh.maslihat по адресу Енбекшиказахский районный маслихат на информационном интернет &ndash; ресурсе опубликовано более 1600 информационных материалов.</div>\r\n\r\n<div style=\"text-align: justify;\">. Также открыт и введен в действие официальный сайт Енбекшиказахского районного маслихата.</div>\r\n\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n\r\n<div style=\"text-align: center;\"><strong>6. ГЛАСНОСТЬ ДЕЯТЕЛЬНОСТИ МАСЛИХАТА</strong></div>\r\n\r\n<div style=\"text-align: justify;\">&nbsp;Деятельность уполномоченного органа проводится в открытом и прозрачном режиме, все мероприятия проводятся в открытом режиме.</div>\r\n\r\n<div style=\"text-align: justify;\">В работе сессии районного маслихата на постоянной основе принимают участие аким района и его заместители, руководители учреждений, предприятий и государственных органов, а также на постоянной основе предварительно извещаются представителей политических партий, общественных объединений, неправительственных организаций района и средств массовой информации.</div>\r\n\r\n<div style=\"text-align: justify;\">На заседания и слушания, сессии постоянных комиссий, проводимые в выездном формате, в обязательном порядке приглашаются актив соответствующей территории, члены местного сообщества, жители.</div>\r\n\r\n<div style=\"text-align: justify;\">Главный приоритет будет отдан гласности. Для получения оперативной информации населению через районные газеты и интернет-ресурсы публикуются не только сообщения о дате проведения сессии, повестке дня сессии, графике встреч, но и обращения к жителям района об активном участии в подготовке к работе.</div>\r\n\r\n<div style=\"text-align: justify;\">&nbsp;</div>\r\n\r\n<p style=\"text-align:center\"><img src=\"http://enbekshikazak-maslikhat.kz/admin/uploads/images/94924085_R_RRRRSR.png\" style=\"height:50px; text-align:justify; width:150px\" /><img src=\"http://enbekshikazak-maslikhat.kz/admin/uploads/images/94924085_R_RRRRSR.png\" style=\"height:50px; text-align:justify; width:150px\" /><img src=\"http://enbekshikazak-maslikhat.kz/admin/uploads/images/94924085_R_RRRRSR.png\" style=\"height:50px; text-align:justify; width:150px\" /><img src=\"http://enbekshikazak-maslikhat.kz/admin/uploads/images/94924085_R_RRRRSR.png\" style=\"height:50px; text-align:justify; width:150px\" /><img src=\"http://enbekshikazak-maslikhat.kz/admin/uploads/images/94924085_R_RRRRSR.png\" style=\"height:50px; text-align:justify; width:150px\" /></p>\r\n\r\n<p style=\"text-align:center\"><img src=\"http://enbekshikazak-maslikhat.kz/admin/uploads/images/91748206_R13.png\" style=\"height:103px; text-align:center; width:150px\" /></p>\r\n\r\n<p style=\"text-align:center\"><strong><em>Отчет </em></strong><strong><em>секретаря</em></strong><strong><em> Енбекшиказахского районного маслихата Б.Ахметова о проделанной работе районного маслихата за 2017 год</em></strong></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 08 февраля&nbsp; 2018г.</em></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><strong><em>Уважаемые коллеги и земляки!</em></strong></span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2017-й год имеет для Казахстана особое значение. Благодаря политической стабильности и общественному консенсусу мы приступили к модернизации трех базовых сфер &ndash; экономики, политики, сознания.</em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>Во-первых</strong>, в Послании дан импульс новому этапу технологической, инфраструктурной модернизации.</em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>Во-вторых</strong>, конституционная реформа установила более точный баланс ветвей власти.</em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>В-третьих</strong>, мы развернули процесс духовной модернизации и обновления национального сознания.</em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; По сути, эти три направления являются системной триадой казахстанской модернизации.</em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; И прежде чем зачитать свой информационный доклад&nbsp; о проделанной работе за отчетный период по организации деятельности районного маслихата&nbsp; согласно повестке дня, разрешите отметит что районный маслихат работает в новых условиях, с большой ответственностью. Депутаты стали проявлять больше инициативы. Я считаю это позитивным явлением. </em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<em>&nbsp;Все важнейшие вопросы, касающиеся жизнедеятельности нашего района, в обязательном порядке проходят через районный маслихат. Народные избранники уже достаточно хорошо осмыслили свое место и роль в обществе, научились принципиально отстаивать интересы избирателей. </em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Теперь мы поставили перед собой задачу не только выражать волю населения, но и определять меры для ее реализации,&nbsp; постоянно контролировать их осуществление.</em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp; &nbsp; &nbsp; &nbsp;Согласно Конституции Республики </em><em>Казахстан - Местные представительные органы - маслихаты выражают волю населения соответствующих административно-территориальных единиц и с учетом общегосударственных интересов определяют меры, необходимые для ее реализации, контролируют их осуществление.</em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">&nbsp; &nbsp; &nbsp;&nbsp;<em>Свою деятельность маслихат&nbsp;&nbsp; Енбекшиказахского района осуществляет на основе Конституции Республики Казахстан, Закона Республики Казахстан &laquo;О местном государственном управлении и самоуправлении в Республике Казахстан&raquo; и Регламента работы маслихата. </em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">&nbsp; &nbsp; &nbsp;&nbsp;<em>Так же, маслихат в своей деятельности руководствуется Стратегией&nbsp; &laquo;Казахстан-2050&raquo;, &laquo;Плана нации 100 конкретных шагов&raquo;, </em><em>также Посланием Президента</em> <em>&nbsp;страны </em>&nbsp;<em>Н</em><em>.</em><em>А.Назарбаева народу Казахстана</em><em> и комплексных программ социально-экономического развития&nbsp; нашего района.&nbsp; </em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">&nbsp; &nbsp; &nbsp;&nbsp;<em>На выборах в&nbsp; марте 2016 года в&nbsp; маслихат района&nbsp; избраны 23 депутатов. В составе депутатского корпуса руководители предприятий, индивидуальные предприниматели, руководители учреждений образования, здравоохранения и др.&nbsp; </em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">&nbsp; &nbsp; &nbsp; &nbsp;<em>Из <strong>23 депутатов</strong> районного маслихата &nbsp;<strong>8 - ранее</strong> уже избирались в районный маслихат разного созывов, их наработанный опыт значительно повышает эффективность деятельности депутатского корпуса.&nbsp; </em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">&nbsp; &nbsp; &nbsp; &nbsp;<em>По гендерному составу 19 мужчин (82,6%) и&nbsp; 4 женщин (17,4, %) По национальному составу: казахов 17, русских - 2, чечен &ndash; 1,&nbsp; уйгур - 2, курд -1. Все имеют высшее образование . </em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">&nbsp; &nbsp; &nbsp;<em>Сессия как основная форма деятельности местных представительных органов</em><em>, занимает главное место в нашей работе. </em><em>На сессиях&nbsp; маслихата&nbsp; утверждаются Программы развития территории района,&nbsp; экономического и социального развития района, бюджеты на соответствующие периоды, и отчеты об их выполнении,&nbsp; отчеты акима, руководителей исполнительных органов, утверждаются правила оказания социальной и жилищной помощи отдельным категориям нуждающихся граждан города, решаются иные животрепещущие вопросы. </em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">&nbsp; &nbsp;&nbsp;<em>За отчетный &nbsp;период 2017 года&nbsp; маслихатом района проведено <strong>12 сессий</strong>, из них 6<strong> очередных и 6 внеочередных</strong>. Всего рассмотренным &nbsp;вопросам принято <strong>53 решения</strong>.</em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp; &nbsp; &nbsp;Депутаты&nbsp;&nbsp; маслихата активно участвуют в работе сессий и&nbsp; прениях по рассматриваемым вопросам.&nbsp; В среднем явка депутатов на сессии составила <strong>86,9 %,&nbsp; всего 13,1 %</strong> &mdash;&nbsp; отсутствовали&nbsp; только по уважительным причинам</em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">&nbsp; &nbsp;&nbsp;<em>Большое внимание в ходе сессионной деятельности уделяется вопросу формирования бюджета, его исполнению, внесению в него изменений и дополнений. Одной из важных прерогатив маслихата является утверждение бюджета и отчетов о его исполнении. В 2017 году на внеочередных сессиях <strong>7 раз</strong> рассмотрен вопрос &mdash; О внесении изменений в решение Енбекшиказахского районного&nbsp; маслихата.</em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">&nbsp; &nbsp; &nbsp;<em>В соответствии с Указом Президента РК от 18 января 2006 года № 19 &laquo;О проведении отчетов акимов перед маслихатами&raquo; были заслушаны <strong>отчет акима района</strong>, а также отчеты акимов <strong>трех</strong> сельских округов&nbsp; (Балтабай, Турген, Асы).</em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">&nbsp; &nbsp;&nbsp;<em>Так же, <strong>2 раза</strong> было заслушано отчет руководителя <strong>местной полицейской службы</strong> Енбекшиказахского районного управления внутренних дел. И 6 раз было рассмотрено отчеты заместителей акима района и отделов.</em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">&nbsp; &nbsp; &nbsp;<em>Согласно Закона Республики Казахстан &laquo;О нормативных правовых актах&raquo; решения сессии, усматривающие правовые нормы, проходят государственную регистрацию в областном Департаменте юстиции и вносятся в Республиканский центр правовой информации. За отчетный период принято <strong>15 нормативных правовых</strong> <strong>актов</strong>,&nbsp; все они прошли государственную регистрацию и являются документом обязательного исполнения на территории района. Правовых документов, возвращенных без регистрации не было. За этот период 2017 года утратил силу <strong>8 нормативно правовых актов</strong>, в связи с изменением законов.</em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>В районном маслихате работают 4 постоянные комиссии:</em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><strong><em>1.</em></strong><em> Постоянная комиссия по вопросам развития отраслей социального направления и законности, правопорядка, общественного согласия, местного государственного управления и самоуправления.</em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><strong><em>2. </em></strong><em>Постоянная комиссия по вопросам экономики, бюджета и финансов.</em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><strong><em>3. </em></strong><em>Постоянная комиссия по вопросам поддержки агропромышленного сектора, малого и среднего бизнеса и деловой активности, индустриально-инновационного развития.</em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><strong><em>4. </em></strong><em>Постоянная комиссия по вопросам развития энергетики, общей инфраструктуры и жилищно-коммунального хозяйства.</em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>Согласно закону РК &laquo;О местном государственном управлении и самоуправлении в РК&raquo;&nbsp; основное назначение постоянных комиссии районного маслихата - предварительное рассмотрение и подготовка вопросов к сессиям, содействие проведению в жизнь решений маслихата. </em></span></span></p>\r\n\r\n<div>\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>За текущий период было проведено всего <strong>10 заседаний</strong>, из них <strong>5 совместных и 4 выездные, 1 раздельное</strong> слушания постоянных комиссий. Наиболее активно обсуждаемыми вопросами на постоянных комиссиях являются проблемы, касающиеся жилищно-коммунального хозяйства, о готовности объектов социальной сферы к отопительному периоду. Большое внимание уделяют депутаты решению проблем благоустройства населенных пунктов района, перевозке учащихся в школы из отдаленных пунктов района.</em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>Кроме того, по инициативе депутатов маслихата для подготовки вопросов на заседании постоянных комиссии была создана рабочая группа, и все вопросы был изучены с выездом в сельские округа. </em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>Депутатская комиссия согласно графика посетила все округа и выявила ряд недостатков. На заседаниях постоянных комиссии были приняты постановление, где было поручено акимам сельских округов и руководительям отделов об устранении всех замечании и недостатков, указанных &nbsp;в справке депутатской комиссии и предложениях. Все данные вопросы взяты на постоянный контроль.</em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>Все мероприятия&nbsp;&nbsp;районного маслихата проводились в режиме открытости и прозрачности.&nbsp; В работе сессий принимали участие аким района и его заместители, депутаты областного маслихата, Почетные граждане района и председатели местного сообщества, ветеранов и аксакалов сельских округов, а также представители общественных организаций и политических партий, представители прессы (СМИ). </em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>Также приглашались руководители&nbsp;&nbsp;предприятий, организаций, других государственных органов. Для получения оперативной информации от населения через районную газету заранее информируем население о дате проведения и повестке дня сессии.&nbsp; Своевременно без срывов, в указанные сроки готовились материалы сессии и заседаний постоянных комиссий: проекты решении и постановлений, справки доклады, содоклады и выступления.</em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;В ходе подготовки заседании с выездом в сельокруга проводились &nbsp;сходы граждан, круглые столы, многочисленные встречи и беседы, что дало эффективные и положительные результаты в изучении проблем и реальной оценки ситуации, проводимых работ и правильном принятии решений.&nbsp; Такая организация работы районного маслихата по подготовке вопросов на дала положительные результаты в решении поднимаемых вопросов и проблем.&nbsp; </em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;Депутаты маслихата принимали активное участие в подготовке и проведении сессий и заседаний постоянных комиссий. Заблаговременно в помощь им готовится памятки по повестке дня и графики посещения сельокругов. При подготовки вопросов заранее приглашаются руководители исполнительных органов, чтобы депутаты получили исчерпывающую информацию. Все это положительно сказывается на активности участия народных избранников. В среднем на каждой сессии и заседаниях постоянных комиссии выступают не менее трех депутатов, три и более приглашенных из числа руководителей исполнительных органов и других организаций. </em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>По рассмотренным вопросам на сессиях принимаются решения, а на заседаниях постоянных комиссий постановления, где даются конкретные поручения и рекомендации госорганам.&nbsp; Активнее используется депутатами право на <strong>депутатский запрос</strong>. </em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>Всего в исполнительные органы&nbsp; направлено <strong>11 депутатских запросов</strong>. Исполнение&nbsp; депутатских запросов взято на контроль постоянными комиссиями и аппаратом маслихата.</em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>Сегодня, о значимости Маслихатов в системе управления государством, свидетельствует укрепление их контрольно-политических механизмов утверждения бюджетов, социально экономических программ и планов развития&nbsp;&nbsp; районов, а также внедрение принципа отчетности акимов перед депутатами, выборы Акимов сельских округов. </em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>Одним из основных в деятельности депутатов районного маслихата является прием граждан по личным вопросам. Выполнение депутатских полномочий &ndash; это не только участие в работе районного маслихата, сессий и постоянных комиссий, где решаются важные вопросы районного масштаба, но и, что не менее значимо,&nbsp; работа с избирателями. </em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>С начало 6 созыва депутатами районного маслихата в своих избирательных округах граждан по личным вопросам. Многие вопросы&nbsp; жителей решаются оперативно, в ходе приема граждан. Решено <strong>положительно 66,9 %</strong> обращения, по <strong>33,1%</strong> обращения даны разъяснения в соответствии с действующим законодательством. Как показывает анализ по сравнению с предыдущими годами, уменьшилось число обращений по социальным вопросам, обращений по коммунальным вопросам становится больше, в основном это касается работы коммунальных служб энергоснабжения и освещения улиц.&nbsp; </em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>Для эффективной работы депутатов акимами выделены помещения для приема граждан, эти помещения были отремонтированы, оформлены и оснащены согласно требованиям. График приема граждан напечатаны в районной газете &laquo;Енбекшиказах&raquo; и на сайте района. Каждый депутат проводит прием граждан, встречается с избирателями, совместно с исполнительной властью участвует в реализации социальных проектов, держит на контроле выполнение заявленных в предвыборной программе обещаний.</em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>Успешно утвердилась практика участия всех депутатов на встречах с населением, проводимых акимом района, акимами сельских округов. Бывая на этих встречах, депутаты выслушивают проблемы людей, ищут пути их решения. Также депутаты районного маслихата активно участвовали во всех благотворительных мероприятиях и акциях, посвященных&nbsp; Дню Победы, с посещением&nbsp; участников и инвалидов ВОВ,&nbsp; Дню Конституции,&nbsp; Дню пожилых людей, акции &laquo;Дорога в школу&raquo; с оказанием помощи детям сиротам и детям оставшихся без попечения родителей,&nbsp; и т.д.</em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>На первой организационной сессии был утвержден состав депутатской фракции НДП &laquo;Нур Отан&raquo;. Основными направлениями деятельности депутатской фракции стали разработка и утверждение региональной программы по реализации Предвыборной платформы партии, участие в работе общественных приемных филиалов партии, мониторинг решения проблем и выполнения обещаний, общественный контроль над реализацией в районе социальных программ.</em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>&nbsp;Согласно отдельного плана проводит работу и участвуют в работе комиссий по контролю строительства новых объектов инфраструктуры, по ремонту дорог, водопроводов и по другим вопросам.&nbsp; Согласно утверждённому графику, в течение года депутаты &ndash; члены партийной фракции ведут прием граждан в Общественной приемной партии&nbsp; &laquo;Нұр Отан&raquo;. График приемов публикуется в СМИ.</em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><strong><em>В соответствии с 99 шагом&nbsp; </em></strong><em>&nbsp;&laquo;Плана нации &ndash; 100 конкретных шагов&raquo;&nbsp; в районе был создан общественный совет, в который вошли 25 человек из числа представителей госорганов, гражданских активистов и общественных организаций. Следует отметить, согласно Закона РК &laquo;Об общественных советах&raquo;, статьи 13 маслихат отвечает за организационное обеспечение деятельности Общественного Совета. Для повышения информированности членов&nbsp; Общественного совета были проведены смеминары- тренинги по разъяснению закона &laquo;Об общественных Советах в РК&raquo;, &laquo;Общественные советы как форма взаимодействия населения и органов местного самоуправления&raquo;.</em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>Деятельность районного маслихата активно освещается в средствах массовой информации, где опубликовано более 1<strong>30 статей</strong> и сообщении. В них освещены материалы о проведенных сессиях, заседаниях постоянных комиссий и других мероприятиях районного маслихата, с полным анализом рассмотренных вопросов и принятых решениях. </em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>В районной газете &laquo;Еңбекшіқазақ&raquo;, &laquo;ЮиТ&raquo; открыта постоянно действующая рубрика где регулярно освещаются и публикуются принятые решения, постановления, выступления депутатов. Сотрудники газеты принимают участие в работе сессий, заседаниях комиссии, выездных заседаниях, блиц-интервью с депутатами, оперативно выпускают материалы на полосы газет.</em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>На 2018 год районный маслихат разрабатывает открытие в Интернете свой сайт еnkaz-maslihat.kz,&nbsp; что позволит более оперативно информировать посетителей сайта о деятельности депутатского корпуса. На&nbsp; сайте&nbsp; маслихата также будет размещена&nbsp; веб - страница Общественного Совета. Благодаря социальным сетям будет выстроен механизм эффективной обратной связи с населением. </em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>Всю деятельность работы депутатского корпуса&nbsp; координирует аппарат маслихата Енбекшиказахского района.</em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>Быть&nbsp;депутатом &ndash; огромная ответственность, прежде всего перед избирателями, перед обществом, перед самим собой. Все депутаты районного маслихата четко понимают суть происходящих в обществе процессов, остро чувствуют свою причастность к ним, ясно представляют свою роль и место в решении стоящих перед ними задач.</em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>Сегодня&nbsp; депутаты районного маслихата работают в тесном сотрудничестве с представителями местного сообщества. </em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>На совместных встречах, &laquo;круглых столах&raquo; обмениваясь мнениями, вместе с избирателями депутаты и председатели&nbsp; местного сообщества ищут пути решения существующих проблем.&nbsp; Вместе с этим реализуется идея Главы государства &laquo;Мәңгілік ел&raquo; по укреплению межнационального согласия, дружбы народов и работе с молодежью.</em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>Пользуясь возможностью&nbsp; выражаю благодарность за вклад в развитие нашего&nbsp; района, который сегодня по праву является экономическим лидером в области.</em></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><em><span style=\"font-family:times new roman,times,serif\">Заканчивая выступление, хочу заверить Вас, что сегодня у районного маслихата имеются все возможности совершенствования работы.&nbsp; И мы приложим все свои силы и знания для успешного претворения в жизнь поставленных перед ними задач. &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;</em></span></p>\r\n</div>\r\n\r\n<p style=\"text-align:center\"><img src=\"http://enbekshikazak-maslikhat.kz/admin/uploads/images/94924085_R_RRRRSR.png\" style=\"height:50px; text-align:justify; width:150px\" /><img src=\"http://enbekshikazak-maslikhat.kz/admin/uploads/images/94924085_R_RRRRSR.png\" style=\"height:50px; text-align:justify; width:150px\" /><img src=\"http://enbekshikazak-maslikhat.kz/admin/uploads/images/94924085_R_RRRRSR.png\" style=\"height:50px; text-align:justify; width:150px\" /><img src=\"http://enbekshikazak-maslikhat.kz/admin/uploads/images/94924085_R_RRRRSR.png\" style=\"height:50px; text-align:justify; width:150px\" /><img src=\"http://enbekshikazak-maslikhat.kz/admin/uploads/images/94924085_R_RRRRSR.png\" style=\"height:50px; text-align:justify; width:150px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n', NULL, 1558606101, 'maslikhat');
INSERT INTO `info` (`id`, `title_kz`, `title_ru`, `title_url_kz`, `title_url_ru`, `content_kz`, `content_ru`, `date`, `created_at`, `parent`) VALUES
(16, 'Мәслихат тарихы', 'История маслихата', 'mslihat-tarihy', 'istoriya-maslihata', '<h2 style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><strong>Мәслихат тарихы</strong></span></span></h2>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><strong>ЕҢБЕКШІҚАЗАҚ АУДАНДЫҚ МӘСЛИХАТЫ:&nbsp; ҚАЛЫПТАСУ, ӨСУ, ӨРКЕНДЕУ ЖЫЛДАРЫ</strong></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><img alt=\"\" src=\"/admin/uploads/images/16630-6-v_kyzylorde_nachalsya__ru.jpg\" style=\"height:199px; width:300px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">Қазақстан Республикасы өз Тәуелсіздігін алған алғашқы жылдары еліміздің&nbsp; даму бағытын айқындайтын, қоғамдық қатынастарды жүзеге асыратын сан салалы Заңдар дүниеге келді.&nbsp; Солардың бірі 1993 жылғы&nbsp;10 желтоқсанда қабылданған &laquo;Қазақстан Республикасының жергілікті өкілді және атқарушы органдары туралы&raquo; №4100 Заңы болатын. Осы Заңды негізге ала отырып, 1994 жылы елімізде тұңғыш рет мәслихаттар институты енгізілді.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">&laquo;Қазақстан Республикасындағы сайлау туралы&raquo; Заңына сәйкес өткізілген сайлау қорытындысымен әкімшілік-аумақта тұратын 25&nbsp;азамат (бұрынғы Шелек ауданын қосқанда 53 депутат)&nbsp;5 жыл мерзімге аудандық Мәслихат &ndash; Депутаттар жиналысының депутаты болып сайланды. 1994 жылғы 7 наурызда өткен бірінші шақырылған аудандық Мәслихат &ndash; Депутаттар жиналысының І сессиясында&nbsp; &nbsp;оның құрылымы мен ұйымдық негіздері қаланды.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><strong>1-ші шақырылған</strong> аудандық мәслихаттың жұмысы еліміздегі қайта құрулармен, атқару және өкілді органдардың жаңа құрылымдарын қалыптастыру кезеңіңе тұстас болды. Мәслихат депутаттары аудан бюджетінің тиімді және мақсатты жұмсалуын қадағалау, аудан халқын әлеуметтік қорғау бағдарламаларының орындалуын бақылау, шаруа қожалықтары мен шағын кәсіпкерлікті дамыту және басқа да көптеген өзекті мәселелерді ауданның атқарушы органымен бірлесіп атқарды.</span></span></p>\r\n\r\n<div style=\"clear:both;\">\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>1&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Абдрасулов Полат Садыкович</em></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>2&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Айсабаев Сеилхан</em></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>3&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Адилова Шахинур Садыковна</em></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>4&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Алимжанов Аблай</em></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>5&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Алимжанов Сакен</em></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>6&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Аширов Анварбек</em></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>7&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Бабыкбаев Толеген Ешенгалиевич</em></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>8&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Байжолданов Бекен</em></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>9&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Бекбосынов Мырзахан</em></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>10&nbsp;&nbsp;&nbsp;&nbsp; Бектенов Тауекель</em></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>11&nbsp;&nbsp;&nbsp;&nbsp; Брусков Николай</em></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>12&nbsp;&nbsp;&nbsp;&nbsp; Брушкевич Земфира Михайловна</em></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>13&nbsp;&nbsp;&nbsp;&nbsp; Булатов Акан Екешевич</em></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>14&nbsp;&nbsp;&nbsp;&nbsp; Ваисов Нурмухамбет Серикбаевич</em></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>15&nbsp;&nbsp;&nbsp;&nbsp; Воробьев Алексей</em></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>16&nbsp;&nbsp;&nbsp;&nbsp; Даулетбакова Бахытгуль Тойганбаевна</em></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><em>17&nbsp;&nbsp;&